import React, { useState } from "react";
import axios from "axios";
import { Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Radio, RadioGroup, FormControlLabel, FormControl, FormLabel } from '@material-ui/core';
import { v4 as uuidv4 } from 'uuid';
import "./CreateQuiz.css";

export default function Mymodal({ closeModal }) {
  const [activeStep, setActiveStep] = useState(0);
  const [quizName, setQuizName] = useState("");
  const [quizType, setQuizType] = useState(null);
  const [option, setOption] = useState("");
  const [url, setUrl] = useState("");
  const [both, setBoth] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState("");
  const [currentOptions, setCurrentOptions] = useState(["", ""]);
  const [numOfQuestion, setnumOfQuestion] = useState(1);
  const [isVisible, setIsVisible] = useState(true);
  const [shareableLink, setShareableLink] = useState('');
  const [continueToNextStep, setContinueToNextStep] = useState(false);

  const handleQuizNameChange = (e) => {
    setQuizName(e.target.value);
  };

  const handleClick = (quizTypeARG) => {
    setQuizType(quizTypeARG);
  };

  const handleQuestionChange = (index, e) => {
    setCurrentQuestion(e.target.value);
    const newQuestions = [...questions];
    newQuestions[index] = e.target.value;
    setQuestions(newQuestions);
  };

  const handleOptionChange = (index, e) => {
    const newOptions = [...currentOptions];
    newOptions[index] = e.target.value;
    setCurrentOptions(newOptions);
  };

  const handleAddQuestions = () => {
    if (numOfQuestion < 5) {
      setQuestions((prevQuestions) => [...prevQuestions, ""]);
      setnumOfQuestion(numOfQuestion + 1);
      if (numOfQuestion === 4) {
        setIsVisible(!isVisible);
      }
    }
  };

  const handleDeleteQuestion = (index) => {
    setQuestions(questions.filter((question, i) => i !== index));
  };

  const handleAddOption = () => {
    setCurrentOptions((prevOptions) => [...prevOptions, ""]);
  };

  const handleContinueClick = () => {
    if (activeStep === 0) {
      setActiveStep((prevStep) => prevStep + 1);
    } else if (activeStep === 1) {
      setQuestions((prevQuestions) => [...prevQuestions, { question: currentQuestion, options: currentOptions }]);
      setActiveStep((prevStep) => prevStep + 1);
    }
    if (continueToNextStep) {
      console.log('Continuing to the next step...');
    }
  };

  const handleCreateQuiz = () => {
    const quizId = uuidv4();
    const link = `https://example.com/quiz/${quizId}`;
    setShareableLink(link);
    setContinueToNextStep(true);
  };

  async function QnAClick(e) {
    e.preventDefault();

    try {
      await axios("http://localhost:4000/createquiz/qna", {
        action: " ",
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        data: JSON.stringify({
          questions: [
            {
              quiz_name: quizName,
              quiz_type: quizType,
              question: String,
              options: [
                {
                  option: String,
                  iscorrect: Boolean
                }
              ]
            }
          ]
        })
      });
    } catch (e) {
      console.log(e);
    }
  }

  return (
    <Dialog open onClose={closeModal} maxWidth="md" fullWidth>
      <DialogTitle>
        {activeStep === 0 && "Create a new Quiz"}
        {activeStep === 1 && "Add Questions"}
        {activeStep === 2 && "Quiz Published"}
      </DialogTitle>
      <DialogContent>
        {activeStep === 0 && (
          <div className="action-buttons">
            <TextField
              className="quiz-name"
              placeholder="Quiz name"
              value={quizName}
              onChange={handleQuizNameChange}
              fullWidth
            />
            <div className="display-flex buttons">
              <p className="quiz-type">Quiz Type</p>
              <Button
                onClick={() => handleClick("qna")}
                className="qna-button"
                style={{
                  backgroundColor: quizType === "qna" ? "#60B84B" : "white",
                  color: quizType === "qna" ? "white" : "#757575",
                }}
              >
                Q & A
              </Button>
              <Button
                onClick={() => handleClick("poll")}
                className="poll-type-button"
                style={{
                  backgroundColor: quizType === "poll" ? "#60B84B" : "white",
                  color: quizType === "poll" ? "white" : "#757575",
                }}
              >
                Poll Type
              </Button>
            </div>
          </div>
        )}
        {activeStep === 1 && (
          <div className="action-buttons">
            <div className="question-options">
              <div className="box1">
                <div className="add-button-div">
                  <div className="first-render">
                    <Button className="question-number">1</Button>
                    <Button className="add-button" onClick={handleAddQuestions}>
                      +
                    </Button>
                  </div>
                  <div className="render-parent">
                    {questions.map((Question, index) => (
                      <div className="render" key={index}>
                        <div>
                          <Button className="question-number" value={Question}>
                            {index + 2}
                          </Button>
                          <Button className="delete-button" onClick={() => handleDeleteQuestion(index)}>
                            x
                          </Button>
                          {index === questions.length - 1 && isVisible && (
                            <Button onClick={handleAddQuestions} className="add-button">
                              +
                            </Button>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
                <div className="inline-question">
                  <TextField
                    onChange={(e) => handleQuestionChange(questions.length - 1, e)}
                    placeholder="Q & A Question"
                    fullWidth
                  />
                </div>
                <div className="optionType">
                  <FormControl component="fieldset">
                    <FormLabel component="legend">Option Type</FormLabel>
                    <RadioGroup row onChange={handleOptionTypeChange} name="optionradio" value={placeholder}>
                      <FormControlLabel value="text" control={<Radio />} label="Text" />
                      <FormControlLabel value="url" control={<Radio />} label="Image" />
                      <FormControlLabel value="both" control={<Radio />} label="Text & Image" />
                    </RadioGroup>
                  </FormControl>
                </div>
              </div>
              <div className="options">
                <div className="qnaOptions">
                  {currentOptions.map((option, index) => (
                    <div key={index} style={{ display: 'flex', alignItems: 'center' }}>
                      <Radio className="radio" />
                      <TextField
                        className="text"
                        value={option}
                        onChange={(e) => handleOptionChange(index, e)}
                        placeholder="Text"
                        fullWidth
                      />
                    </div>
                  ))}
                  <Button onClick={handleAddOption}>Add Option</Button>
                </div>
              </div>
            </div>
          </div>
        )}
        {activeStep === 2 && (
          <div className="action-buttons">
            <div className="questions">
              <p className="published">Congrats, your Quiz is Published!</p>
              <p>Shareable link: {shareableLink}</p>
            </div>
          </div>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={closeModal} color="secondary">
          Cancel
        </Button>
        <Button
          onClick={() => {
            if (activeStep === 1) {
              handleCreateQuiz();
            }
            handleContinueClick();
          }}
          color="primary"
          disabled={activeStep === 0 && !quizType}
        >
          {activeStep === 0 ? 'Continue' : 'Create Quiz'}
        </Button>
      </DialogActions>
    </Dialog>
  );
}
