import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

export default function Login() {
  const [activeForm, setActiveForm] = useState("signUp");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  async function signUpClick(e) {
    e.preventDefault();
    setActiveForm("signUp");

    try {
      await axios.post("http://localhost:4000/user/save", {
        name: name,
        email: email,
        password: password,
      });
    } catch (e) {
      console.log(e);
    }
  }

  async function logInClick(e) {
    e.preventDefault();

    try {
      const res = await axios.post("http://localhost:4000/user/find", {
        email: email,
        password: password,
      });
      if (res.status === 200) {
        // Store the logged-in user's email in local storage or state
        localStorage.setItem("userEmail", email);
        navigate("/dashboard");
      }
    } catch (e) {
      console.log(e);
    }
  }

  return (
    <div>
      <div className="container">
        <div id="quizzieBox">
          <h1>Quizzie</h1>

          <div className="slides">
            <div className="slidesHead">
              <div className="inline">
                <button
                  id="signUpButton"
                  onClick={signUpClick}
                  className={activeForm === "signUp" ? "activeButton" : ""}
                >
                  Sign Up
                </button>
              </div>
              <div className="inline">
                <button
                  id="logInButton"
                  onClick={() => setActiveForm("logIn")}
                  className={activeForm === "logIn" ? "activeButton" : ""}
                >
                  Log In
                </button>
              </div>
            </div>

            <div className="slidesBody">
              {activeForm === "logIn" && (
                <div className="logIn inline">
                  <div>
                    <p className="inline emailin">Email</p>
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    ></input>
                  </div>
                  <div>
                    <p className="inline passin">Password</p>
                    <input
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                    ></input>
                  </div>
                  <div>
                    <button
                      type="submit"
                      className="blueButton"
                      onClick={(e) => logInClick(e)}
                    >
                      Log In
                    </button>
                  </div>
                </div>
              )}
              {/* SignUp form code */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
