import React, { useState, useEffect } from 'react';
import Navbar from "../../components/Navbar";
import { Link } from "react-router-dom";
import "./Timer.css";

const TimerComponent = () => {
    const [timerOn, setTimerOn] = useState(false);
    const [timerDuration, setTimerDuration] = useState(0);

    useEffect(() => {
        let intervalId;

        if (timerOn) {
            intervalId = setInterval(() => {
                setTimerDuration((prevDuration) => prevDuration - 1);
            }, 1000);
        }

        return () => {
            clearInterval(intervalId);
        };
    }, [timerOn]);

    const handleStartTimer = (duration) => {
        setTimerDuration(duration);
        setTimerOn(true);
    };

    const handleStopTimer = () => {
        setTimerOn(false);
    };

    const handleResetTimer = () => {
        setTimerDuration(0);
        setTimerOn(false);
    };

    const formatTime = (time) => {
        const minutes = Math.floor(time / 60);
        const seconds = time % 60;
        return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
    };

    return (
        <div className="Timer-container">
            <div className='timer-option'>
                <h3>Timer</h3>
                <button onClick={() => handleStartTimer(0)}>OFF</button>
                <button onClick={() => handleStartTimer(5)}>5 sec</button>
                <button onClick={() => handleStartTimer(10)}>10 sec</button>
                <div className="timer-display">
                    <p>{formatTime(timerDuration)}</p>
                    {timerOn && <button onClick={handleStopTimer}>Stop</button>}
                    {!timerOn && timerDuration > 0 && <button onClick={handleResetTimer}>Reset</button>}
                </div>
            </div>
        </div>
    );
};

export default function Timer() {
    return <TimerComponent />;
}
