import React, { useState } from "react";
import Navbar from "../../components/Navbar";
import "./CreateQuiz.css";

export default function CreateQuiz() {
  const [showPopup, setShowPopup] = useState(false);
  const [quizTitle, setQuizTitle] = useState("");
  const [quizQuestions, setQuizQuestions] = useState([]);

  const handlePopupClick = () => {
    setShowPopup(true);
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const handleQuizTitleChange = (event) => {
    setQuizTitle(event.target.value);
  };

  const handleAddQuestion = () => {
    setQuizQuestions([...quizQuestions, { title: "", options: [] }]);
  };

  const handleQuestionTitleChange = (index, event) => {
    const newQuestions = [...quizQuestions];
    newQuestions[index].title = event.target.value;
    setQuizQuestions(newQuestions);
  };

  const handleAddOption = (index) => {
    const newQuestions = [...quizQuestions];
    newQuestions[index].options.push("");
    setQuizQuestions(newQuestions);
  };

  const handleOptionChange = (questionIndex, optionIndex, event) => {
    const newQuestions = [...quizQuestions];
    newQuestions[questionIndex].options[optionIndex] = event.target.value;
    setQuizQuestions(newQuestions);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle quiz submission here
  };

  return (
    <div>
      <div className="navbar">
        <Navbar />
      </div>
      <div className="page">
        <button onClick={handlePopupClick}>Create Quiz</button>
        {showPopup && (
          <div className="popup">
            <div className="popup-content">
              <h2>Create Quiz</h2>
              <form onSubmit={handleSubmit}>
                <label>
                  Quiz Title:
                  <input
                    type="text"
                    value={quizTitle}
                    onChange={handleQuizTitleChange}
                  />
                </label>
                {quizQuestions.map((question, index) => (
                  <div key={index}>
                    <label>
                      Question {index + 1}:
                      <input
                        type="text"
                        value={question.title}
                        onChange={(event) => handleQuestionTitleChange(index, event)}
                      />
                    </label>
                    {question.options.map((option, optionIndex) => (
                      <div key={optionIndex}>
                        <label>
                          Option {optionIndex + 1}:
                          <input
                            type="text"
                            value={option}
                            onChange={(event) =>
                              handleOptionChange(index, optionIndex, event)
                            }
                          />
                        </label>
                      </div>
                    ))}
                    <button type="button" onClick={() => handleAddOption(index)}>
                      Add Option
                    </button>
                  </div>
                ))}
                <button type="button" onClick={handleAddQuestion}>
                  Add Question
                </button>
                <button type="submit">Submit Quiz</button>
              </form>
              <button onClick={handlePopupClose}>Close</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
