import "./CreateQuiz.css";
import { useState } from "react";

const Mymodal = ({ closeModal }) => {
  const [isActive, setIsActive] = useState();
  const [quizId, setQuizId] = useState(0);
  const [quizName, setQuizName] = useState();
  const [quizType, setQuizType] = useState();
  const [activeQuestion, setActiveQuestion] = useState(0)
  const [selectedAnswer, setSelectedAnswer] = useState('')
  const [result, setResult] = useState({
    score: 0,
    correctAnswers: 0,
    wrongAnswers: 0,
  })
  const [questions, setQuestions] = useState([]);

  const handleClick = () => {
    setIsActive(!isActive);
  };

  const handleQuizTypeChange = (type) => {
    setQuizType(type);
  }

  const handleContinueClick = () => {
    setQuestions((prevQuestions) => [...prevQuestions, { question: '', options: [] }]);
    setActiveQuestion((prevActiveQuestion) => prevActiveQuestion + 1);
  }


  return (
    <div>
      <div class="modal-wrapper"></div>
      <div class="modal-container">
        <div class="quiz-name-type">
          <input class="quiz-name" placeholder="Quiz name" value={quizName} onChange={(e) => setQuizName(e.target.value)}></input>

          <div class="display-flex buttons">
            <p class="quiz-type">Quiz Type</p>
            <button
              onClick={handleQuizTypeChange("Q & A")}
              class="qna-button"
              style={{
                backgroundColor: quizType === "Q & A" ? "#60B84B" : "white",
                color: quizType === "Q & A" ? "white" : "#757575",
              }}
            >
              Q & A
            </button>
            <button
              onClick={handleQuizTypeChange("Poll")}
              class="poll-type-button"
              style={{
                backgroundColor: isActive ? "white" : "#60B84B",
                color: isActive ? "#757575" : "white",
              }}
            >
              Poll Type
            </button>
          </div>
          <div class="action-buttons">
            <button class="cancel" onClick={closeModal}>
              Cancel
            </button>
            <button class="continue">Continue</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Mymodal;
